import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Open the search modal
    await page.click('button.close-modal'); // Click to open the search modal
    await page.waitForSelector('#searchInput'); // Wait for the search input to be visible

    // Take screenshot after opening the search modal
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_search_modal_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_search_modal_opened.png'));

    // Click the remove button (×) on the first recent search item
    await page.waitForSelector('.search-item:first-child .remove-search'); // Wait for the first recent search item to be visible
    await page.click('.search-item:first-child .remove-search'); // Click the remove button

    // Take screenshot after removing the first recent search item
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_removed_first_recent_search.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_removed_first_recent_search.png'));

    return {
      success: true,
      action: "Removed the first recent search item from the search modal",
      extractedData: {},
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

