import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click "Your Story" button using a different selector strategy
    await page.waitForSelector('div.stories'); // Wait for stories section to load
    const yourStoryButton = await page.$('div.stories .story'); // Select the first story (your story button)
    await yourStoryButton.click();

    // Wait for the create story modal to appear
    await page.waitForSelector('#createPostModal', { state: 'visible' });

    // Take a screenshot of the modal to verify it opened
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_create_story_modal.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_create_story_modal.png'));

    // Extract meaningful data to verify task completion
    const modalVisible = await page.isVisible('#createPostModal');

    return {
      success: true,
      action: "Clicked 'Your Story' button and verified create story modal opened",
      extractedData: { modalVisible },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
