import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the first user suggestion to be visible and click the follow button
    await page.waitForSelector('.suggestion-item:first-child .follow-btn');
    await page.click('.suggestion-item:first-child .follow-btn');

    // Wait for the button text to change to "Following"
    await page.waitForFunction(() => {
      const button = document.querySelector('.suggestion-item:first-child .follow-btn');
      return button && button.textContent === 'Following';
    });

    // Take a screenshot after clicking the follow button
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_following.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_following.png'));

    return {
      success: true,
      action: "Clicked follow button on the first user in suggestions and verified text changed to 'Following'",
      extractedData: {},
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the suggestions to load and click the follow button on the first user
    await page.waitForSelector('.suggestion-item:first-child .follow-btn', { timeout: 5000 });
    const followButton = await page.$('.suggestion-item:first-child .follow-btn');
    await followButton.click();

    // Wait for the button text to change to "Following"
    await page.waitForFunction(() => {
      const button = document.querySelector('.suggestion-item:first-child .follow-btn');
      return button && button.textContent === "Following";
    }, { timeout: 5000 });

    // Take a screenshot after following the user
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_following.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_following.png'));

    return {
      success: true,
      action: "Clicked follow button on the first user and verified text change",
      extractedData: {
        buttonText: await followButton.evaluate(button => button.textContent)
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

