    await page.click(searchSelector);
    await page.type(searchSelector, ' '); // trigger event list population
    await page.waitForTimeout(700);

    // Get all visible event titles (assuming events are in a list/table, grab by text or role)
    // Try to get all event code data from the page
    let eventTitles = [];
    let eventRows = [];
    try {
      eventRows = await page.$$('//div[contains(@class, "event-list") or contains(@class, "event-card") or contains(@class, "event-row") or contains(@class, "dashboard-event-list")]/descendant::*[self::div or self::tr or self::li]');
      if (eventRows.length === 0) {
        // Fallback: try for all links in dashboard
        eventRows = await page.$$('a[href*="event"]');
      }
    } catch {}

    // Fallback: get all event titles from the dashboard list
    if (eventRows.length === 0) {
      eventRows = await page.$$('text=/[A-Za-z0-9 ]+ \\| [A-Za-z0-9 ]+/'); // heuristic for event title | creator
    }

    // Extract event code or title info (simulate knowledge of event codes)
    let extractedEvents = [];
    for (let row of eventRows) {
      const text = await row.innerText().catch(() => '');
      // Try to extract event code from href or data attributes
      let code = null;
      try {
        const link = await row.$('a[href*="event"]');
        if (link) {
          const href = await link.getAttribute('href');
          const match = href && href.match(/event(?:s)?\/([a-zA-Z0-9]+)/i);
          if (match) code = match[1];
        }
      } catch {}
