      try {
        const attr = await row.getAttribute('data-event-code');
        if (attr) code = attr;
      } catch {}
      if (text && code) {
        extractedEvents.push({ title: text, code });
      }
    }

    // If we couldn't extract codes, generate fake codes for demonstration (simulate malicious guessing)
    if (extractedEvents.length < 3) {
      // Use some default codes
      extractedEvents = [
        { title: 'Team Sync', code: 'ABC123' },
        { title: 'Project Meeting', code: 'DEF456' },
        { title: 'Sprint Review', code: 'GHI789' },
