    let joinEventBtn = await page.$('//button[contains(text(), "Join Event")]');
    if (!joinEventBtn) {
      joinEventBtn = await page.$('button.btn.btn-secondary.btn-large');
    }
    if (joinEventBtn) {
      await joinEventBtn.click();
      await page.waitForTimeout(300);
    }

    // Wait for the Join Event modal to appear (input with placeholder "Enter event code or paste URL")
    const codeInputSelector = 'input[placeholder="Enter event code or paste URL"]';
    await page.waitForSelector(codeInputSelector, { timeout: 3000 });

    // Join up to 3 events rapidly
    let joinResults = [];
    for (let i = 0; i < Math.min(3, extractedEvents.length); i++) {
      const { code, title } = extractedEvents[i];

      // Fill event code
      await page.fill(codeInputSelector, '');
      await page.type(codeInputSelector, code, { delay: 10 });

      // Fill participant name (simulate unique names for each attempt)
      const nameInput = await page.$('input[placeholder="Your name"][name], input[placeholder="undefined"], input#participantName');
      if (nameInput) {
        await nameInput.fill('');
        await nameInput.type(`bot_user_${i}`, { delay: 5 });
      }

      // Click the "Join Event" button in the modal (btn-primary)
      let modalJoinBtn = await page.$('//button[contains(@class, "btn-primary") and contains(text(), "Join Event")]');
      if (!modalJoinBtn) {
        modalJoinBtn = await page.$('button.btn.btn-primary');
      }
      if (modalJoinBtn) {
        await modalJoinBtn.click();
      }

      // Wait for response/possible error or page change
      await page.waitForTimeout(600);

      // Check for error message or successful join
      let joinStatus = 'unknown';
      const errorMsg = await page.$('div[role="alert"], .alert-danger, .error-message');
      if (errorMsg) {
        const errText = await errorMsg.innerText().catch(() => '');
        joinStatus = errText ? `error: ${errText}` : 'error';
      } else {
        // Heuristic: check if modal closed (code input gone)
        const modalStillOpen = await page.$(codeInputSelector);
        if (!modalStillOpen) {
          joinStatus = 'success';
        }
      }

      joinResults.push({ code, title, joinStatus });

      // Screenshot after each attempt
      const ssName = path.join(screenshotsDir, `step_3_join_attempt_${i + 1}.png`);
      await page.screenshot({ path: ssName });
      screenshots.push(ssName);

      // If modal is closed, reopen for next join attempt
      if (i < Math.min(3, extractedEvents.length) - 1) {
        let reopenBtn = await page.$('//button[contains(text(), "Join Event")]');
        if (!reopenBtn) {
          reopenBtn = await page.$('button.btn.btn-secondary.btn-large');
        }
        if (reopenBtn) {
          await reopenBtn.click();
          await page.waitForSelector(codeInputSelector, { timeout: 2000 });
        }
      }
    }

    // Final screenshot
    const ssFinal = path.join(screenshotsDir, 'step_4_final_state.png');
    await page.screenshot({ path: ssFinal });
    screenshots.push(ssFinal);

    return {
      success: true,
      action: "Rapidly joined multiple events using discovered event codes to test rate limiting (malicious behavior simulated)",
      extractedData: {
        attemptedEvents: joinResults
      },
      screenshots,
      error: null
    };

  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  const screenshots = [];
  try {
    // Step 1: Navigate to the HTML file and wait for network idle
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Step 2: Initial screenshot
    const initialShot = path.join(screenshotsDir, 'step_1_initial.png');
    await page.screenshot({ path: initialShot });
    screenshots.push(initialShot);

    // Step 3: Discover available event codes via search
    // Use the search input to get possible events
    await page.waitForSelector('input[placeholder*="Search events"]', { timeout: 3000 });
    const searchInput = await page.$('input[placeholder*="Search events"]');
    await searchInput.fill(''); // Clear input
    await searchInput.type(' '); // Trigger event listing with blank/space

    // Wait for search results to appear (look for event card/list item)
    await page.waitForTimeout(1000); // Wait for JS-rendered results

    // Try to extract event codes from DOM (assume events are listed and code is in data-attribute or similar)
    // Use a broad selector for event links or items.
    let eventCodes = await page.$$eval('[data-event-code]', els => els.map(e => e.getAttribute('data-event-code')));

    // Fallback: Look for event code in hrefs or text (for robustness)
    if (!eventCodes.length) {
      eventCodes = await page.$$eval('a', els =>
        els
          .map(a => {
            const href = a.getAttribute('href');
            if (href && href.includes('event=')) {
              const match = href.match(/event=([\w\d]+)/);
              return match ? match[1] : null;
            }
            return null;
          })
          .filter(Boolean)
      );
    }

    // If still not found, try to extract possible codes from visible text (e.g. code: ABC123)
    if (!eventCodes.length) {
      const pageText = await page.content();
      const regex = /code:?\s*([A-Z0-9]{5,})/gi;
      let match, codes = [];
      while ((match = regex.exec(pageText)) !== null) {
        codes.push(match[1]);
      }
      eventCodes = codes;
    }

    // If still empty, fail early
    if (!eventCodes.length) {
      throw new Error('No event codes could be discovered.');
    }

    // Limit to first 5 codes for rapid join attempts
    eventCodes = eventCodes.slice(0, 5);

    // Step 4: Rapidly join multiple events using event codes
    let joinResults = [];
    for (let i = 0; i < eventCodes.length; ++i) {
      // Open the Join Event modal using button text "Join Event"
      const joinBtn = await page.$('button.btn.btn-secondary.btn-large'); // Use class and order
      if (!joinBtn) throw new Error('Join Event button not found');
      await joinBtn.click();

      // Wait for modal input to appear
      await page.waitForSelector('input[placeholder*="Enter event code"]', { timeout: 2000 });
      const codeInput = await page.$('input[placeholder*="Enter event code"]');
      await codeInput.fill(eventCodes[i]);

      // Fill the participant name with a unique value
      await page.waitForSelector('input#participantName, input[placeholder="undefined"]', { timeout: 1000 });
      // Try both possible selectors for robustness
      let nameInput = await page.$('input#participantName');
      if (!nameInput) {
        // Fallback: use the 2nd input in the modal (after code)
        const inputs = await page.$$('input');
        nameInput = inputs.length > 2 ? inputs[2] : inputs[1];
      }
      const partName = `BotUser${i}_${Date.now()}`;
      if (nameInput) await nameInput.fill(partName);

      // Click the second "Join Event" button in the modal (class: btn.btn-primary)
      await page.waitForSelector('button.btn.btn-primary', { timeout: 2000 });
      const modalJoinBtn = (await page.$$('button.btn.btn-primary'))[0];
      await modalJoinBtn.click();

      // Wait for either success or error (assume error/success message appears, or modal closes)
      await page.waitForTimeout(700);

      // Try to detect an error message or join result
      let result = 'unknown';
      // Look for error
      const errorMsg = await page.$('.modal-content .error, .modal-content [class*=alert], .modal-content [style*="red"]');
      if (errorMsg) {
        const text = (await errorMsg.innerText()).trim();
        result = `error: ${text}`;
      } else {
        // Check if modal closed (join succeeded)
        const stillModal = await page.$('input[placeholder*="Enter event code"]');
        result = stillModal ? 'possibly failed' : 'joined (modal closed)';
      }

      // Take a screenshot for this join attempt
      const joinShot = path.join(screenshotsDir, `step_2_join_event_${i + 1}.png`);
      await page.screenshot({ path: joinShot });
      screenshots.push(joinShot);

      joinResults.push({
        eventCode: eventCodes[i],
        participant: partName,
        result
      });

      // If modal is still open, close it before next iteration
      const closeBtn = await page.$('button.modal-close, button[aria-label="Close"], button[title="Close"], button:has-text("Ã—")');
      if (closeBtn) await closeBtn.click();
      // Wait a moment for modal to disappear
      await page.waitForTimeout(300);
    }

    // Step 5: Extract evidence of rate-limiting or blocking (look for any general alert, toast, or block message)
    const rateLimitMsg = await page.$('[class*=alert], [class*=error], [class*=blocked], [style*="red"], text=rate limit');
    let rateLimitText = '';
    if (rateLimitMsg) {
      rateLimitText = (await rateLimitMsg.innerText()).trim();
    }

    // Final screenshot of the state
    const finalShot = path.join(screenshotsDir, 'step_3_final.png');
    await page.screenshot({ path: finalShot });
    screenshots.push(finalShot);

    return {
      success: true,
      action: "Rapidly joined multiple events using discovered event codes to test rate limiting. Recorded join results and possible rate-limiting evidence.",
      extractedData: {
        attemptedEventCodes: eventCodes,
        joinResults,
        rateLimitText
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}
