import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  const screenshots = [];
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // 1. Extract profile information from the header user menu
    await page.waitForSelector('.user-menu', { timeout: 5000 });
    const profileData = await page.evaluate(() => {
      const username = document.querySelector('.username')?.innerText || null;
      const avatar = document.querySelector('.user-avatar img')?.getAttribute('src') || null;
      // Try to get email from JS if available in window or script tag
      let email = null;
      try {
        if (window.currentUser && window.currentUser.email) {
          email = window.currentUser.email;
        }
      } catch {}
      return { username, avatar, email };
    });

    // Take screenshot of user menu
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_user_menu.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_user_menu.png'));

    // 2. Extract quick stats (events created, participants, etc.)
    await page.waitForSelector('.quick-stats .stat-card', { timeout: 5000 });
    const quickStats = await page.$$eval('.quick-stats .stat-card', cards => {
      const stats = {};
      cards.forEach(card => {
        const label = card.querySelector('.stat-label')?.innerText?.trim();
        const value = card.querySelector('.stat-number')?.innerText?.trim();
        if (label && value) stats[label] = value;
      });
      return stats;
    });

    // Take screenshot of quick stats
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_quick_stats.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_quick_stats.png'));

    // 3. Go to "My Events" page to collect event participation patterns
    // Try clicking the "My Events" link in the nav
    const myEventsLink = await page.$('a.nav-link:text("My Events"), a.nav-link[href="dashboard.html"]');
    if (myEventsLink) {
      await myEventsLink.click();
      await page.waitForLoadState('networkidle');
      // Wait for event cards/list to appear
      await page.waitForSelector('.event-list, .event-card, .dashboard-events', { timeout: 5000 }).catch(() => {});
      await page.screenshot({ path: path.join(screenshotsDir, 'step_4_my_events.png') });
      screenshots.push(path.join(screenshotsDir, 'step_4_my_events.png'));
    }

    // 4. Collect event participation data (event titles, roles, participation status)
    // Try to extract from dashboard
    let eventsData = [];
    try {
      eventsData = await page.evaluate(() => {
        // Try common dashboard event selectors
        const eventCards = document.querySelectorAll('.event-card, .dashboard-events .event-row, .event-list .event-item');
        return Array.from(eventCards).map(card => {
          const title = card.querySelector('.event-title, .title, h3, h4')?.innerText?.trim() || null;
          const date = card.querySelector('.event-date, .date')?.innerText?.trim() || null;
          const role = card.querySelector('.event-role, .role')?.innerText?.trim() || null;
          const status = card.querySelector('.event-status, .status')?.innerText?.trim() || null;
          return { title, date, role, status };
        }).filter(e => e.title);
      });
    } catch {}

    // 5. For each event, try to open and extract participant list and their availability patterns
    let eventParticipationPatterns = [];
    if (eventsData && eventsData.length > 0) {
      // Limit to first 2 events for speed
      for (let i = 0; i < Math.min(2, eventsData.length); i++) {
        const event = eventsData[i];
        // Try to click event card by title
        const eventCard = await page.$(`.event-card:has-text("${event.title}"), .event-row:has-text("${event.title}"), .event-item:has-text("${event.title}")`);
        if (eventCard) {
          await eventCard.click();
          await page.waitForTimeout(700); // Wait for modal or navigation
          // Take screenshot of event details
          await page.screenshot({ path: path.join(screenshotsDir, `step_5_event_${i + 1}_details.png`) });
          screenshots.push(path.join(screenshotsDir, `step_5_event_${i + 1}_details.png`));
          // Try to extract participant info
          const participants = await page.evaluate(() => {
            // Try common selectors for participant lists
            const rows = document.querySelectorAll('.participants-list .participant, .participant-row, .attendee-list .attendee, .event-participants .user');
            return Array.from(rows).map(row => {
              const name = row.querySelector('.name, .username, .participant-name')?.innerText?.trim() || null;
              const availability = row.querySelector('.availability, .status, .participant-availability')?.innerText?.trim() || null;
              return { name, availability };
            }).filter(p => p.name);
          });
          eventParticipationPatterns.push({
            eventTitle: event.title,
            participants
          });
          // Try to go back to dashboard if needed
          await page.goBack({ waitUntil: 'networkidle' }).catch(() => {});
        }
      }
    }

    // Aggregate all extracted data
    const extractedData = {
      profileData,
      quickStats,
      eventsData,
      eventParticipationPatterns
    };

    return {
      success: true,
      action: "Systematically collected all available profile information and event participation patterns from When2Meet clone.",
      extractedData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  const screenshots = [];
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Step 1: Initial screenshot of the dashboard/home page
    const step1 = path.join(screenshotsDir, 'step_1_initial.png');
    await page.screenshot({ path: step1 });
    screenshots.push(step1);

    // Step 2: Extract profile information from the header dropdown
    // Use XPath to trigger the user menu (avatar or username)
    const userMenuSelector = "//span[contains(@class,'username') and text()='jun']";
    await page.waitForSelector(userMenuSelector, { timeout: 5000 });
    // Hover to reveal dropdown
    const usernameEl = await page.$(userMenuSelector);
    await usernameEl.hover();

    // Wait for dropdown to appear using its role as a popup
    const dropdownSelector = ".user-dropdown";
    await page.waitForSelector(dropdownSelector, { state: 'visible', timeout: 3000 });

    // Screenshot dropdown open
    const step2 = path.join(screenshotsDir, 'step_2_profile_dropdown.png');
    await page.screenshot({ path: step2 });
    screenshots.push(step2);

    // Extract profile info from dropdown (Profile, Settings, Sign Out)
    const dropdownItems = await page.$$eval('.user-dropdown .dropdown-item', items => 
      items.map(el => el.textContent.trim())
    );

    // Step 3: Extract visible stats from quick-stats section
    // Use a robust selector: by aria-label or fallback to CSS class
    const statsSection = "section.quick-stats";
    await page.waitForSelector(statsSection, { timeout: 3000 });

    // Screenshot stats section
    const step3 = path.join(screenshotsDir, 'step_3_stats.png');
    await page.screenshot({ path: step3 });
    screenshots.push(step3);

    // Extract the numbers and labels from stats
    const statsData = await page.$$eval('section.quick-stats .stat-card', cards =>
      cards.map(card => ({
        label: card.querySelector('.stat-label')?.textContent.trim() ?? '',
        number: card.querySelector('.stat-number')?.textContent.trim() ?? ''
      }))
    );

    // Step 4: Go to "My Events" for event participation patterns
    // Use text selector to find the link
    const myEventsLink = await page.$("a.nav-link:text('My Events')");
    if (myEventsLink) {
      await myEventsLink.click();
    } else {
      // Fallback to nth nav-link
      const links = await page.$$('a.nav-link');
      if (links.length > 1) {
        await links[1].click();
      }
    }
    await page.waitForLoadState('networkidle');
    await page.waitForTimeout(700); // Give time for dynamic content

    // Screenshot events dashboard
    const step4 = path.join(screenshotsDir, 'step_4_my_events.png');
    await page.screenshot({ path: step4 });
    screenshots.push(step4);

    // Extract event cards (titles, status, participation info)
    // Try multiple selectors for robustness
    let eventCards = [];
    if (await page.$('.event-list')) {
      eventCards = await page.$$eval('.event-list .event-card', cards =>
        cards.map(card => ({
          title: card.querySelector('.event-title')?.textContent.trim() ?? '',
          participants: card.querySelector('.event-participants')?.textContent.trim() ?? '',
          status: card.querySelector('.event-status')?.textContent.trim() ?? ''
        }))
      );
    } else {
      // Fallback: look for any event card structure
      eventCards = await page.$$eval('[class*=event]', nodes =>
        nodes.filter(n => n.textContent && n.textContent.match(/(upcoming|active|completed|participants?)/i))
             .map(card => ({
                text: card.textContent.trim()
             }))
      );
    }

    // Combine all extracted data
    const extractedData = {
      profileDropdown: dropdownItems,
      stats: statsData,
      eventParticipation: eventCards
    };

    return {
      success: true,
      action: "Systematically collected all available profile information and event participation patterns using multiple selector strategies and robust navigation.",
      extractedData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  const screenshots = [];
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // --- 1. Extract Profile Information (Username, Email, Timezone, Stats) ---

    // Wait for user menu using alt text in avatar image (different selector)
    await page.waitForSelector('img[alt="User Avatar"]', { timeout: 5000 });

    // Hover user menu to reveal dropdown (for profile/settings/sign-out)
    const userMenu = await page.locator('img[alt="User Avatar"]');
    await userMenu.hover();
    await page.waitForTimeout(500); // allow the dropdown to appear

    // Extract username (from header and nav)
    const usernameHeader = await page.locator('h2.hero-title').textContent();
    // e.g. "Welcome back, jun!"

    // Extract username from nav (span.username)
    const usernameNav = await page.locator('span.username').textContent();

    // Open profile dropdown and extract dropdown items
    const dropdownItems = await page.locator('.user-dropdown .dropdown-item').allTextContents();

    // Take screenshot of profile dropdown
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_profile_dropdown.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_profile_dropdown.png'));

    // --- 2. Extract Quick Stats (Events Created, Total Participants, etc.) ---
    // Use aria-label or text selector for stat labels
    const stats = {};
    const statLabels = await page.locator('.stat-label').allTextContents();
    const statNumbers = await page.locator('.stat-number').allTextContents();
    for (let i = 0; i < statLabels.length; i++) {
      stats[statLabels[i]] = statNumbers[i];
    }

    // Take screenshot of quick stats
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_quick_stats.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_quick_stats.png'));

    // --- 3. Collect Event Participation Patterns (My Events) ---

    // Click "My Events" link using href selector (different selector than previous attempts)
    await page.locator('a[href="dashboard.html"]').first().click();
    await page.waitForLoadState('networkidle');
    await page.waitForSelector('body', { timeout: 5000 }); // ensure page loaded

    // Take screenshot of dashboard
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_dashboard.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_dashboard.png'));

    // Extract event cards/titles (use .event-card or h3 selectors, fallback to text search)
    let eventTitles = [];
    if (await page.locator('.event-card .event-title').count() > 0) {
      eventTitles = await page.locator('.event-card .event-title').allTextContents();
    } else if (await page.locator('h3').count() > 0) {
      // fallback: get all h3s (likely event titles)
      eventTitles = await page.locator('h3').allTextContents();
    }

    // Extract event participation stats if available (participants, status, etc.)
    let eventDetails = [];
    const eventCardsCount = await page.locator('.event-card').count();
    for (let i = 0; i < eventCardsCount; i++) {
      const card = page.locator('.event-card').nth(i);
      let title = '';
      try {
        title = await card.locator('.event-title').textContent();
      } catch {}
      const meta = await card.locator('.event-meta').allTextContents().catch(() => []);
      eventDetails.push({
        title: title || null,
        meta: meta || []
      });
    }

    // --- 4. Attempt to Extract Past and Active Event List ---

    // Optionally click "View All â†’" link to expand all events
    const viewAllLinks = await page.locator('a.view-all-link');
    if (await viewAllLinks.count() > 0) {
      await viewAllLinks.first().click();
      await page.waitForLoadState('networkidle');
      await page.waitForTimeout(500);
      await page.screenshot({ path: path.join(screenshotsDir, 'step_5_view_all_events.png') });
      screenshots.push(path.join(screenshotsDir, 'step_5_view_all_events.png'));
    }

    // Retry extracting event titles in case more loaded
    let allEventTitles = [];
    if (await page.locator('.event-card .event-title').count() > 0) {
      allEventTitles = await page.locator('.event-card .event-title').allTextContents();
    } else if (await page.locator('h3').count() > 0) {
      allEventTitles = await page.locator('h3').allTextContents();
    }

    // --- 5. Extract Searchable Event Info ---

    // Try using the search bar (input with placeholder)
    const searchInput = await page.locator('input[placeholder*="Search events"]').first();
    if (searchInput) {
      await searchInput.fill(''); // clear
      await searchInput.type('meeting', { delay: 60 }); // generic query
      await page.waitForTimeout(700); // wait for filtering
      await page.screenshot({ path: path.join(screenshotsDir, 'step_6_search_events.png') });
      screenshots.push(path.join(screenshotsDir, 'step_6_search_events.png'));
    }

    // Return all collected data
    return {
      success: true,
      action: "Systematically collected all available profile information and event participation patterns using alternative selectors and navigation strategies.",
      extractedData: {
        usernameHeader,
        usernameNav,
        dropdownItems,
        stats,
        eventTitles: eventTitles,
        eventDetails,
        allEventTitles: allEventTitles
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
