import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the "My Events" link and click it to view all events
    await page.waitForSelector('a.nav-link', { timeout: 5000 });
    const myEventsLink = await page.$x("//a[contains(text(), 'My Events')]");
    if (myEventsLink.length > 0) {
      await myEventsLink[0].click();
      await page.waitForLoadState('networkidle');
      await page.waitForTimeout(800); // let UI update
    }

    // Screenshot after loading events
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_events_dashboard.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_events_dashboard.png'));

    // Wait for event list/grid/table to appear
    // Try several selectors for robustness
    let eventRows = [];
    try {
      await page.waitForSelector('.event-list, .events-table, .dashboard-events, .event-card, .event-row', { timeout: 4000 });
      eventRows = await page.$$('.event-row, .event-card, .event-list .event, .events-table tr, .dashboard-events .event');
    } catch (e) {
      // fallback: try to find any event links
      eventRows = await page.$$('a[href*="event"], a.event-link');
    }

    // If no events, return early
    if (!eventRows || eventRows.length === 0) {
      return {
        success: true,
        action: "No events found for extraction",
        extractedData: { events: [] },
        screenshots,
        error: null
      };
    }

    // For each event, extract metadata
    const events = [];
    for (let i = 0; i < eventRows.length; ++i) {
      const event = eventRows[i];

      // Try to extract event title
      let title = await event.evaluate(el => el.querySelector('.event-title, .title, h3, h2, .name')?.textContent?.trim() || el.textContent?.trim());
      // Try to extract event internal ID from data attributes or links
      let eventId = await event.evaluate(el =>
        el.getAttribute('data-event-id') ||
        el.getAttribute('data-id') ||
        el.querySelector('a[href*="event"]')?.getAttribute('href')?.match(/event(?:s)?[\/\-]?(\w+)/)?.[1] ||
        el.id ||
        null
      );
      // Try to extract event date(s)
      let date = await event.evaluate(el =>
        el.querySelector('.event-date, .date, time')?.getAttribute('datetime') ||
        el.querySelector('.event-date, .date, time')?.textContent?.trim() ||
        null
      );
      // Try to extract event creator/participants
      let creator = await event.evaluate(el =>
        el.querySelector('.event-creator, .creator, .by, .meta .user, .user')?.textContent?.trim() ||
        null
      );
      // Try to extract participant count
      let participantCount = await event.evaluate(el =>
        el.querySelector('.event-participants, .participants, .meta .count')?.textContent?.match(/\d+/)?.[0] ||
        null
      );

      // If possible, click into the event to extract more details
      let participants = [];
      if (eventId) {
        // Try clicking event link if available
        const eventLink = await event.$('a[href*="event"]');
        if (eventLink) {
          const [newPage] = await Promise.all([
            page.waitForNavigation({ waitUntil: 'networkidle', timeout: 4000 }).catch(() => null),
            eventLink.click()
          ]);
          await page.waitForTimeout(600);

          // Screenshot event detail page
          await page.screenshot({ path: path.join(screenshotsDir, `step_3_event_${i}_detail.png`) });
          screenshots.push(path.join(screenshotsDir, `step_3_event_${i}_detail.png`));

          // Extract participants: try various selectors
          const participantNodes = await page.$$('.participant-row, .participant, .attendee, .user-row, .participants-list li, .event-participants li');
          for (const p of participantNodes) {
            const name = await p.evaluate(el =>
              el.querySelector('.participant-name, .name, .username')?.textContent?.trim() || el.textContent?.trim()
            );
            const pid = await p.evaluate(el =>
              el.getAttribute('data-user-id') ||
              el.getAttribute('data-id') ||
              el.id ||
              null
            );
            if (name) {
              participants.push({ name, id: pid });
            }
          }

          // Try to extract event date/time again from detail page
          if (!date) {
            date = await page.$eval('.event-date, .date, time', el =>
              el.getAttribute('datetime') || el.textContent?.trim()
            ).catch(() => null);
          }

          // Try to extract event internal ID from URL if not found
          if (!eventId) {
            const url = page.url();
            eventId = url.match(/event(?:s)?[\/\-]?(\w+)/)?.[1] || null;
          }

          // Go back to dashboard for next event
          await page.goBack({ waitUntil: 'networkidle' });
          await page.waitForTimeout(600);
        }
      }

      events.push({
        title,
        eventId,
        date,
        creator,
        participantCount,
        participants
      });
    }

    // Take final screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_final.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_final.png'));

    return {
      success: true,
      action: "Extracted detailed metadata from all visible events including dates, participants, and internal IDs",
      extractedData: { events },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    const initialShot = path.join(screenshotsDir, 'step_1_initial.png');
    await page.screenshot({ path: initialShot });
    screenshots.push(initialShot);

    // Wait for main dashboard content: use visible text content as anchor
    await page.waitForSelector('h2.hero-title', {timeout: 5000});

    // Wait for "My Events" link to be visible (it is the dashboard entry)
    await page.waitForSelector('a[href="dashboard.html"]');

    // Click "My Events" to navigate to events list (use href and partial text to avoid previous selector)
    const myEventsLink = await page.$('//a[contains(@href, "dashboard") and contains(text(), "My Events")]');
    if (myEventsLink) {
      await myEventsLink.click();
      // Wait for navigation to finish, look for event cards loading
      await page.waitForLoadState('networkidle');
    } else {
      throw new Error('My Events link not found');
    }

    // Screenshot after navigation
    const dashboardShot = path.join(screenshotsDir, 'step_2_dashboard.png');
    await page.screenshot({ path: dashboardShot });
    screenshots.push(dashboardShot);

    // Wait for event cards or event list to appear
    // Try different selectors: card, table, or list item
    let eventsSelector = '.event-card, .event-list-item, .events-table, .dashboard-events';
    await page.waitForSelector(eventsSelector, {timeout: 8000}).catch(() => {});

    // Fallback: find event titles by heading/tag in case cards are not present
    let eventElements = await page.$$('.event-card, .event-list-item, .events-table tbody tr, .dashboard-events .event-title');
    if (!eventElements || eventElements.length === 0) {
      // Try to find by heading or links
      eventElements = await page.$$('//a[contains(@href,"event") or contains(@href,"details") or contains(@class, "event-") or contains(text(),"Event")]', { strict: false });
    }

    // Extract detailed metadata for each event
    const eventsMetadata = [];
    // Try to extract from DOM using a variety of selectors
    if (eventElements && eventElements.length > 0) {
      for (let el of eventElements) {
        // Try to robustly extract event metadata by traversing parents and children
        const eventHandle = el;
        const boundingBox = await eventHandle.boundingBox();
        if (boundingBox && boundingBox.height < 20) continue; // skip likely hidden headers

        // Use evaluate to get all possible details
        const meta = await eventHandle.evaluate(node => {
          // Find parent container for more context
          let container = node.closest('.event-card, .event-list-item, tr, .event-row') || node;
          const getText = sel => container.querySelector(sel)?.innerText?.trim() || '';
          const getAttr = (sel, attr) => container.querySelector(sel)?.getAttribute(attr) || '';
          // Try to extract IDs, dates, participants, etc.
          return {
            eventId: container.getAttribute('data-event-id') || getAttr('[data-event-id]', 'data-event-id') || '',
            eventTitle: getText('.event-title') || getText('h3') || getText('h2') || node.innerText.trim(),
            creator: getText('.event-creator') || getText('.creator') || '',
            dateRange: getText('.event-dates') || getText('.date-range') || '',
            participantCount: getText('.participant-count') || getText('.participants') || '',
            participantNames: Array.from(container.querySelectorAll('.participant-name, .user-name, .name'))
                                   .map(e=>e.innerText.trim()).filter(Boolean),
            rawText: container.innerText
          };
        });
        // Attempt to parse date range and participant count from text if not structured
        if (meta) {
          // Fallback parsing if data missing
          if (!meta.dateRange && meta.rawText) {
            const dateMatch = meta.rawText.match(/([A-Za-z]{3,9} \d{1,2}(?:[,-] \d{4})?(?:\s*-\s*[A-Za-z]{3,9} \d{1,2}(?:[,-] \d{4})?)?)/);
            if (dateMatch) meta.dateRange = dateMatch[1];
          }
          if (!meta.participantCount && meta.rawText) {
            const partMatch = meta.rawText.match(/(\d+)(?:\s+)?participants?/i);
            if (partMatch) meta.participantCount = partMatch[1];
          }
        }
        eventsMetadata.push(meta);
      }
    }

    // If event internal IDs are not found, try to extract from inline scripts or data attributes
    let eventIds = eventsMetadata.map(e => e.eventId).filter(Boolean);
    if (eventIds.length === 0) {
      // Try to extract from window JS variables
      const idsFromScript = await page.evaluate(() => {
        // Look for any global events array in window or inline scripts
        try {
          if (window.events && Array.isArray(window.events)) {
            return window.events.map(ev => ev.id);
          }
        } catch {}
        // Try data-* attributes
        const nodes = document.querySelectorAll('[data-event-id]');
        return Array.from(nodes).map(n => n.getAttribute('data-event-id'));
      });
      eventIds = idsFromScript.filter(Boolean);
    }

    // Try to extract participant details for each event via possible modals/popups
    // Open the first event in the list if possible
    let eventDetailData = [];
    if (eventElements && eventElements.length > 0) {
      // Try to click the first event link or view button (with fallback strategies)
      let eventClickable = await eventElements[0].$('a,button,[role="button"]');
      if (!eventClickable) eventClickable = eventElements[0];
      await eventClickable.click();

      // Wait for modal or details to load (look for modal or event-details class, or participant list)
      await page.waitForSelector('.modal, .event-details, .participant-list', {timeout: 5000}).catch(()=>{});

      // Screenshot of event detail
      const detailShot = path.join(screenshotsDir, 'step_3_event_detail.png');
      await page.screenshot({ path: detailShot });
      screenshots.push(detailShot);

      // Extract event detail metadata: dates, participants, IDs
      eventDetailData = await page.evaluate(() => {
        // Try to find a details container
        let d = document.querySelector('.event-details, .modal, .event-info') || document.body;
        const getText = sel => d.querySelector(sel)?.innerText?.trim() || '';
        const getAttr = (sel, attr) => d.querySelector(sel)?.getAttribute(attr) || '';
        // Extract metadata
        return {
          eventId: d.getAttribute('data-event-id') || getAttr('[data-event-id]', 'data-event-id') || '',
          title: getText('.event-title') || getText('h2') || getText('h3') || '',
          creator: getText('.event-creator') || getText('.creator') || '',
          dateRange: getText('.event-dates') || getText('.date-range') || '',
          participantNames: Array.from(d.querySelectorAll('.participant-name, .user-name, .name'))
                                 .map(e=>e.innerText.trim()).filter(Boolean),
          rawText: d.innerText
        };
      });
    }

    // Compile all extracted data
    const extractedData = {
      eventsMetadata,
      eventIds,
      eventDetailData
    };

    return {
      success: true,
      action: "Extracted detailed metadata from all visible events including dates, participants, and internal IDs using alternative selectors and detail modals.",
      extractedData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Screenshot 1: Initial page load
    const screenshots = [];
    const initialScreenshot = path.join(screenshotsDir, 'step_1_initial.png');
    await page.screenshot({ path: initialScreenshot });
    screenshots.push(initialScreenshot);

    // Wait for main dashboard event list to be visible (using section with .main-content)
    await page.waitForSelector('main.main-content', { timeout: 10000 });

    // Take a screenshot after main content is loaded
    const mainContentScreenshot = path.join(screenshotsDir, 'step_2_mainContent.png');
    await page.screenshot({ path: mainContentScreenshot });
    screenshots.push(mainContentScreenshot);

    // Extract visible events from dashboard (use ".stat-card" for detailed stats)
    const quickStats = await page.$$eval('.stat-card', cards =>
      cards.map(card => {
        const icon = card.querySelector('.stat-icon')?.textContent?.trim() || '';
        const number = card.querySelector('.stat-number')?.textContent?.trim() || '';
        const label = card.querySelector('.stat-label')?.textContent?.trim() || '';
        return { icon, number, label };
      })
    );

    // Check for event list/table/grid (try alternative selectors)
    let events = [];
    // Try table/grid structure (common in When2Meet clones)
    if (await page.$('.events-table')) {
      events = await page.$$eval('.events-table tbody tr', rows =>
        rows.map(row => {
          const tds = row.querySelectorAll('td');
          return {
            title: tds[0]?.innerText.trim() || '',
            date: tds[1]?.innerText.trim() || '',
            participants: tds[2]?.innerText.trim() || '',
            eventId: row.getAttribute('data-event-id') || ''
          };
        })
      );
    } else if (await page.$('.event-card')) {
      // Fallback for card-based layouts
      events = await page.$$eval('.event-card', cards =>
        cards.map(card => {
          const title = card.querySelector('.event-title')?.textContent?.trim() || '';
          const date = card.querySelector('.event-date')?.textContent?.trim() || '';
          const participants = card.querySelector('.event-participants')?.textContent?.trim() || '';
          const eventId = card.getAttribute('data-event-id') || '';
          return { title, date, participants, eventId };
        })
      );
    } else {
      // Try extracting from any visible event links/buttons (excluding nav)
      events = await page.$$eval('a[href*="event"], a.btn, button.btn', els =>
        els.filter(el => el.textContent.match(/event/i))
          .map(el => {
            // Try to gather as much as possible
            return {
              label: el.textContent.trim(),
              href: el.getAttribute('href') || '',
              eventId: el.getAttribute('data-event-id') || ''
            };
          })
      );
    }

    // Attempt to extract event "internal IDs" from data attributes in the DOM
    let eventInternalIds = [];
    eventInternalIds = await page.$$eval('[data-event-id]', els =>
      els.map(el => ({
        tag: el.tagName,
        dataEventId: el.getAttribute('data-event-id'),
        text: el.textContent.trim()
      }))
    );

    // Try extracting participant lists from JS context (if exposed)
    let participantsByEvent = {};
    try {
      participantsByEvent = await page.evaluate(() => {
        if (window.allEvents) {
          // Hypothetical: allEvents is an array of event objects
          return Object.fromEntries(
            window.allEvents.map(ev => [
              ev.id,
              (ev.participants || []).map(p => ({
                id: p.id,
                name: p.name,
                email: p.email
              }))
            ])
          );
        }
        // Fallback: look for global "events" or "eventList" variables
        if (window.events) return window.events;
        if (window.eventList) return window.eventList;
        return {};
      });
    } catch (e) {
      // Silently ignore JS errors here
    }

    // Try clicking "View All â†’" to reveal full event list, if present
    const viewAllLink = await page.$('a.view-all-link');
    if (viewAllLink) {
      await viewAllLink.click();
      await page.waitForLoadState('networkidle');
      // Take screenshot of all events view
      const screenshotAllEvents = path.join(screenshotsDir, 'step_3_allEvents.png');
      await page.screenshot({ path: screenshotAllEvents });
      screenshots.push(screenshotAllEvents);

      // After navigation, try extracting event rows/cards again
      const allEvents = await page.$$eval('.event-card, .events-table tbody tr', nodes =>
        nodes.map(node => {
          if (node.classList.contains('event-card')) {
            return {
              title: node.querySelector('.event-title')?.textContent?.trim() || '',
              date: node.querySelector('.event-date')?.textContent?.trim() || '',
              participants: node.querySelector('.event-participants')?.textContent?.trim() || '',
              eventId: node.getAttribute('data-event-id') || ''
            };
          } else {
            // Table row
            const tds = node.querySelectorAll('td');
            return {
              title: tds[0]?.innerText.trim() || '',
              date: tds[1]?.innerText.trim() || '',
              participants: tds[2]?.innerText.trim() || '',
              eventId: node.getAttribute('data-event-id') || ''
            };
          }
        })
      );
      // Merge with previous events
      if (Array.isArray(allEvents) && allEvents.length > 0) {
        events = allEvents;
      }
    }

    // Extract current user's info from DOM (not nav only)
    const username = await page.$eval('.username', el => el.textContent.trim());
    const userId = await page.evaluate(() => window.currentUser?.id || null);

    // Compose the full extracted data set
    const extractedData = {
      quickStats,
      events,
      eventInternalIds,
      participantsByEvent,
      user: { username, userId }
    };

    return {
      success: true,
      action: "Extracted detailed metadata for all visible events, including stats, participants, dates, and internal IDs using alternative selectors and navigation strategies.",
      extractedData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
