import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for main dashboard/events section to load
    await page.waitForSelector('.main-content', { timeout: 5000 });

    // Extract all visible event cards (assuming events are listed on dashboard)
    // Try to find event containers by common selectors
    let eventCards = [];
    try {
      // Try multiple strategies for event list
      eventCards = await page.$$eval(
        '.event-card, .dashboard-events-list .event-row, .events-list .event, .my-events-list .event-card',
        cards => cards.map(card => card.outerHTML)
      );
      // Fallback: Try to find events by text
      if (eventCards.length === 0) {
        eventCards = await page.$$eval(
          'a, div',
          nodes => nodes
            .filter(n => n.textContent && /event/i.test(n.textContent))
            .map(n => n.outerHTML)
        );
      }
    } catch (e) {
      // ignore, will handle empty eventCards
    }

    // If no event cards found, try to click "My Events" and retry
    if (eventCards.length === 0) {
      const myEventsLink = await page.$('a.nav-link:text("My Events")');
      if (myEventsLink) {
        await myEventsLink.click();
        await page.waitForLoadState('networkidle');
        await page.waitForTimeout(800);
        await page.screenshot({ path: path.join(screenshotsDir, 'step_2_my_events.png') });
        screenshots.push(path.join(screenshotsDir, 'step_2_my_events.png'));
        eventCards = await page.$$eval(
          '.event-card, .dashboard-events-list .event-row, .events-list .event, .my-events-list .event-card',
          cards => cards.map(card => card.outerHTML)
        );
      }
    }

    // Parse event metadata from event cards
    const eventMetadata = [];
    for (const html of eventCards) {
      // Use DOMParser in browser context to extract fields
      const meta = await page.evaluate(cardHtml => {
        const parser = new DOMParser();
        const doc = parser.parseFromString(cardHtml, 'text/html');
        const meta = {};

        // Title
        let title = doc.querySelector('.event-title, .title, h3, h2');
        meta.title = title ? title.textContent.trim() : '';

        // Date(s)
        let date = doc.querySelector('.event-date, .date, .event-dates, .dates');
        meta.date = date ? date.textContent.trim() : '';

        // Internal ID (try data attributes or href)
        let id = '';
        let root = doc.body.firstElementChild;
        if (root) {
          id = root.getAttribute('data-id') || root.getAttribute('data-event-id') || '';
          if (!id && root.hasAttribute('href')) {
            const href = root.getAttribute('href');
            const match = href && href.match(/event(?:s)?\/?([a-zA-Z0-9_\-]+)/);
            if (match) id = match[1];
          }
        }
        meta.internalId = id;

        // Participants (try avatars, names, or participant count)
        let participants = [];
        let partEls = doc.querySelectorAll('.participant, .participants .name, .avatar[alt], .event-participants .name');
        if (partEls.length) {
          participants = Array.from(partEls).map(e => e.textContent.trim()).filter(Boolean);
        } else {
          // Try to find a count
          let count = doc.querySelector('.participant-count, .participants-count, .event-participants-count');
          if (count) participants = [count.textContent.trim()];
        }
        meta.participants = participants;

        return meta;
      }, html);
      // Only add if at least a title or date is found
      if (meta.title || meta.date || meta.internalId) eventMetadata.push(meta);
    }

    // If possible, click into the first event to extract more detailed metadata
    let detailedEventData = {};
    if (eventMetadata.length > 0) {
      // Try to click the first event link
      let eventLink = await page.$('.event-card a, .dashboard-events-list .event-row a, .events-list .event a, .my-events-list .event-card a');
      if (!eventLink) {
        // Try by text
        const firstTitle = eventMetadata[0].title;
        if (firstTitle) {
          eventLink = await page.$(`text="${firstTitle}"`);
        }
      }
      if (eventLink) {
        await eventLink.click();
        await page.waitForLoadState('networkidle');
        await page.waitForTimeout(800);
        await page.screenshot({ path: path.join(screenshotsDir, 'step_3_event_detail.png') });
        screenshots.push(path.join(screenshotsDir, 'step_3_event_detail.png'));

        // Extract event detail metadata
        detailedEventData = await page.evaluate(() => {
          const meta = {};
          // Title
          let title = document.querySelector('.event-title, h2, h1');
          meta.title = title ? title.textContent.trim() : '';

          // Dates
          let date = document.querySelector('.event-date, .date, .event-dates, .dates');
          meta.date = date ? date.textContent.trim() : '';

          // Internal ID (from URL or data attributes)
          meta.internalId = '';
          if (window.location && window.location.href) {
            const match = window.location.href.match(/event(?:s)?\/?([a-zA-Z0-9_\-]+)/);
            if (match) meta.internalId = match[1];
          }
          let root = document.querySelector('.event-detail, .event-main, [data-event-id]');
          if (root && !meta.internalId) {
            meta.internalId = root.getAttribute('data-id') || root.getAttribute('data-event-id') || '';
          }

          // Participants (names, avatars, emails)
          meta.participants = [];
          let partEls = document.querySelectorAll('.participant, .participants .name, .avatar[alt], .event-participants .name');
          if (partEls.length) {
            meta.participants = Array.from(partEls).map(e => e.textContent.trim()).filter(Boolean);
          } else {
            // Try to find a count
            let count = document.querySelector('.participant-count, .participants-count, .event-participants-count');
            if (count) meta.participants = [count.textContent.trim()];
          }

          // Additional metadata: time slots, status, etc.
          meta.timeSlots = [];
          let slotEls = document.querySelectorAll('.time-slot, .availability-slot, .event-time-slot');
          if (slotEls.length) {
            meta.timeSlots = Array.from(slotEls).map(e => {
              const slot = {};
              slot.time = e.textContent.trim();
              slot.status = e.className;
              slot.dataId = e.getAttribute('data-id') || '';
              return slot;
            });
          }

          return meta;
        });
      }
    }

    // Compose extracted data
    const extractedData = {
      events: eventMetadata,
      detailedEvent: detailedEventData
    };

    return {
      success: true,
      action: "Extracted detailed metadata from all visible events including dates, participants, and internal IDs",
      extractedData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    const screenshots = [];
    // Initial screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the main dashboard events section using a different selector (use aria/role and css)
    await page.waitForSelector('section[aria-label="Upcoming Events"], section.main-content, .container:has(.hero-title)');

    // Scroll to the "My Events" link and click it (use link text and fallback to href)
    let myEventsLink = await page.$('a:has-text("My Events")');
    if (!myEventsLink) {
      myEventsLink = await page.$('a[href="dashboard.html"]');
    }
    if (myEventsLink) {
      await myEventsLink.click();
      await page.waitForTimeout(700); // Wait for navigation or dynamic content
    } else {
      throw new Error('My Events link not found');
    }

    // Wait for events list/table/grid to appear
    // Try multiple strategies: by heading, grid, or card
    await page.waitForSelector('.events-list, .events-table, .event-card, [class*="event"]', { timeout: 3000 });

    // Screenshot after navigation
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_events_list.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_events_list.png'));

    // Extract detailed metadata for all visible events
    // Try cards in .event-card, rows in .events-table, or general [class*="event"]
    const events = await page.$$eval(
      '.event-card, .events-table tr, [class*="event"]:not([class*="avatar"]):not([class*="icon"]):not([class*="btn"])',
      (eventNodes) => {
        // Remove duplicates by event id/title
        const seen = new Set();
        const results = [];
        for (const node of eventNodes) {
          // Use title or data-event-id or aria attributes
          let title = node.querySelector('.event-title, [data-event-title]')?.innerText ||
                      node.getAttribute('data-event-title') ||
                      node.querySelector('td')?.innerText ||
                      node.getAttribute('aria-label') || '';
          title = title.trim();

          if (!title || seen.has(title)) continue;
          seen.add(title);

          // Try to extract metadata
          let date = node.querySelector('.event-date, [data-event-date]')?.innerText ||
                     node.getAttribute('data-event-date') ||
                     '';
          date = date.trim();

          let internalId = node.getAttribute('data-event-id') ||
                           node.id ||
                           node.getAttribute('id') ||
                           '';

          // Try to get participants (either as avatars, names, or count)
          let participants = [];
          let participantsText = node.querySelector('.event-participants, [data-event-participants]')?.innerText ||
                                node.getAttribute('data-event-participants') || '';
          // If comma separated
          if (participantsText && participantsText.includes(',')) {
            participants = participantsText.split(',').map(s => s.trim());
          } else if (participantsText) {
            participants = [participantsText.trim()];
          } else {
            // Try to find avatars or names in subnodes
            const avatars = node.querySelectorAll('[class*="avatar"], .participant-name');
            if (avatars.length) {
              participants = Array.from(avatars).map(el =>
                el.getAttribute('alt') || el.innerText || el.getAttribute('data-username') || ''
              ).filter(Boolean);
            }
          }

          // If table row, try to extract cells
          if (!title && node.tagName === 'TR') {
            const cells = node.querySelectorAll('td');
            if (cells.length) {
              title = cells[0].innerText.trim();
              date = (cells[1]?.innerText || '').trim();
              let p = (cells[2]?.innerText || '').trim();
              if (p) participants = p.split(',').map(s => s.trim());
            }
          }

          // Compose event entry
          results.push({
            title,
            date,
            participants,
            internalId,
            rawHtml: node.outerHTML
          });
        }
        return results;
      }
    );

    // For each event, try to click and get more details (open event modal/page)
    let detailedEvents = [];
    for (const [idx, event] of events.entries()) {
      // Try to find clickable element by event title
      let eventHandle = await page.$(`.event-card:has-text("${event.title}"), tr:has-text("${event.title}"), [class*="event"]:has-text("${event.title}")`);
      if (eventHandle) {
        await eventHandle.click();
        await page.waitForTimeout(600);

        // Wait for modal or event detail section to appear (try modal, dialog, or main detail selectors)
        // Use different selectors: modal, h2, or .event-detail
        const detailSelector = '.modal, .event-detail, .event-info, [role="dialog"]';
        await page.waitForSelector(detailSelector, { timeout: 2000 });

        // Screenshot event detail
        await page.screenshot({ path: path.join(screenshotsDir, `step_3_event_detail_${idx + 1}.png`) });
        screenshots.push(path.join(screenshotsDir, `step_3_event_detail_${idx + 1}.png`));

        // Extract more metadata
        const detailData = await page.$eval(detailSelector, node => {
          // Try to pull all visible key metadata (dates, participants, internal IDs, links, etc)
          let detailTitle = node.querySelector('.event-title, h2, h3')?.innerText || '';
          let detailDate = node.querySelector('.event-date, time')?.innerText || '';
          let rawId = node.getAttribute('data-event-id') || '';
          let participants = [];
          let pNodes = node.querySelectorAll('.participant-name, [data-username], .avatar[alt]');
          participants = Array.from(pNodes).map(el =>
            el.innerText || el.getAttribute('alt') || el.getAttribute('data-username') || ''
          ).filter(Boolean);

          // Try to extract links for sharing/internals
          let internalLinks = Array.from(node.querySelectorAll('a')).map(a => a.href);

          return {
            detailTitle,
            detailDate,
            rawId,
            participants,
            internalLinks
          };
        });

        // Try to close modal if present
        let closeBtn = await page.$('button.modal-close, button:has-text("Ã—"), button[aria-label="Close"]');
        if (closeBtn) {
          await closeBtn.click();
          await page.waitForTimeout(400);
        }

        detailedEvents.push({
          ...event,
          ...detailData
        });
      } else {
        detailedEvents.push(event); // fallback
      }
    }

    // Return all extracted event metadata
    return {
      success: true,
      action: "Extracted detailed metadata from all visible events including dates, participants and internal IDs (with screenshots of event list and detail views)",
      extractedData: {
        events: detailedEvents
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    const initialShot = path.join(screenshotsDir, 'step_1_initial.png');
    await page.screenshot({ path: initialShot });
    screenshots.push(initialShot);

    // Wait for the main event list/dashboard to be visible
    // Use text and aria selectors to avoid previous errors
    await page.waitForSelector('text=Welcome back', { timeout: 7000 });

    // Attempt to navigate to 'My Events' page using link text and href
    const myEventsLink = await page.$('a[href="dashboard.html"]');
    if (myEventsLink) {
      await myEventsLink.click();
      await page.waitForLoadState('networkidle');
      // Wait for possible events section or heading to appear
      await page.waitForSelector('h2, h3, .event-list, .events-table, text=Event', { timeout: 7000 });
      const eventsShot = path.join(screenshotsDir, 'step_2_dashboard.png');
      await page.screenshot({ path: eventsShot });
      screenshots.push(eventsShot);
    }

    // Fallback: If no navigation, look for events in DOM directly (for SPA-like dashboard)
    // Try multiple selectors for event cards
    let eventCards = await page.$$('.event-card');
    if (eventCards.length === 0) {
      eventCards = await page.$$('.event-list .event, .events-table tr, [class*="event-row"]');
    }
    // If still none, try to find tables/lists with "Event" headers
    if (eventCards.length === 0) {
      const rows = await page.$$('tr');
      eventCards = [];
      for (const row of rows) {
        const txt = await row.textContent();
        if (txt && /event/i.test(txt)) eventCards.push(row);
      }
    }

    // If events found, extract detailed metadata for all visible events
    const extractedEvents = [];
    for (const card of eventCards) {
      // Try to get event title
      let title = await card.$eval('h3, h4, .event-title', el => el.textContent.trim()).catch(() => '');
      if (!title) title = (await card.textContent()).split('\n')[0].trim();

      // Try to get date info
      let date = await card.$eval('.event-date, .date, [data-date]', el => el.textContent.trim()).catch(() => '');
      if (!date) {
        // Try to find a date in the text
        const txt = await card.textContent();
        const dateMatch = txt && txt.match(/\b(?:Jan|Feb|Mar|Apr|May|Jun|Jul|Aug|Sep|Oct|Nov|Dec)[a-z]*\s+\d{1,2}(?:,?\s+\d{4})?/i);
        date = dateMatch ? dateMatch[0] : '';
      }

      // Try to extract participants (names, count, avatars)
      const participants = [];
      const participantEls = await card.$$('.participant, .participant-name, [class*="user"], img[alt*="avatar"]');
      for (const pel of participantEls) {
        let pname = await pel.getAttribute('alt').catch(() => null);
        if (!pname) pname = await pel.textContent().catch(() => null);
        if (pname) participants.push(pname.replace(/avatar/i, '').trim());
      }
      // Fallback: look for participant count in text
      if (participants.length === 0) {
        const txt = await card.textContent();
        const countMatch = txt && txt.match(/(\d+)\s+participants?/i);
        if (countMatch) participants.push({ count: parseInt(countMatch[1], 10) });
      }

      // Try to extract internal IDs (data attributes, hrefs, etc)
      let eventId = await card.getAttribute('data-id').catch(() => null);
      if (!eventId) {
        // Try to find link or button with event code/ID
        const link = await card.$('a[href*="event"], button[data-event-id]');
        if (link) {
          eventId = await link.getAttribute('data-event-id').catch(() => null);
          if (!eventId) {
            const href = await link.getAttribute('href').catch(() => null);
            if (href) {
              const idMatch = href.match(/event(?:\/|=)([a-zA-Z0-9_-]+)/);
              eventId = idMatch ? idMatch[1] : null;
            }
          }
        }
      }

      extractedEvents.push({
        title,
        date,
        participants,
        eventId
      });
    }

    // Take screenshot of event details if any events found
    if (extractedEvents.length > 0) {
      const detailsShot = path.join(screenshotsDir, 'step_3_event_details.png');
      await page.screenshot({ path: detailsShot });
      screenshots.push(detailsShot);
    }

    // Also try to extract participant details from the user dropdown if visible
    let userInfo = {};
    const avatar = await page.$('.user-avatar img');
    if (avatar) {
      const username = await page.$eval('.username', el => el.textContent.trim()).catch(() => null);
      const avatarSrc = await avatar.getAttribute('src').catch(() => null);
      userInfo = { username, avatarSrc };
    }

    // Compose extractedData
    const extractedData = {
      events: extractedEvents,
      userInfo
    };

    return {
      success: true,
      action: "Extracted detailed metadata for all visible events, including dates, participants, and internal IDs using alternative selectors and fallback strategies. Also extracted user profile info.",
      extractedData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
