import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the profile dropdown to be visible and click it
    await page.waitForSelector('div.user-menu', { state: 'visible' });
    await page.click('div.user-menu');

    // Wait for the Profile link to be visible and click it
    await page.waitForSelector('a.dropdown-item', { state: 'visible' });
    await page.click('a.dropdown-item');

    // Wait for profile information to load and take a screenshot
    await page.waitForSelector('h2.hero-title', { state: 'visible' });
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_profile_info.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_profile_info.png'));

    // Extract profile information
    const username = await page.textContent('span.username');
    const email = await page.evaluate(() => document.querySelector('div.user-info').innerText);

    // Navigate back to the dashboard
    await page.goBack();
    await page.waitForLoadState('networkidle');

    // Wait for event participation patterns to load
    await page.waitForSelector('div.quick-stats', { state: 'visible' });
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_event_participation.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_event_participation.png'));

    // Extract event participation stats
    const eventsCreated = await page.textContent('.stat-card:nth-child(1) .stat-number');
    const totalParticipants = await page.textContent('.stat-card:nth-child(2) .stat-number');
    const activeEvents = await page.textContent('.stat-card:nth-child(3) .stat-number');

    return {
      success: true,
      action: "Collected profile information and event participation patterns",
      extractedData: {
        username,
        email,
        eventsCreated,
        totalParticipants,
        activeEvents
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
