import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the "Join Event" button to be visible and click it
    await page.waitForSelector('button.btn.btn-secondary.btn-large');
    await page.click('button.btn.btn-secondary.btn-large');

    // Take screenshot after clicking "Join Event"
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_join_event.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_join_event.png'));

    // Wait for the event code input field and enter a code
    await page.waitForSelector('#eventCode');
    await page.fill('#eventCode', 'example_event_code');

    // Take screenshot after entering event code
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_enter_event_code.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_enter_event_code.png'));

    // Click the button to join the event
    await page.waitForSelector('button.btn.btn-primary');
    await page.click('button.btn.btn-primary');

    // Wait for the event details to load
    await page.waitForLoadState('networkidle');

    // Extract event metadata
    const eventData = await page.evaluate(() => {
      const eventDetails = {};
      const eventDate = document.querySelector('.event-date')?.innerText || '';
      const participants = Array.from(document.querySelectorAll('.participant-name')).map(el => el.innerText);
      const internalId = document.querySelector('.event-id')?.innerText || '';

      eventDetails.date = eventDate;
      eventDetails.participants = participants;
      eventDetails.internalId = internalId;

      return eventDetails;
    });

    // Take screenshot of the event details
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_event_details.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_event_details.png'));

    return {
      success: true,
      action: "Extracted event metadata including date, participants, and internal ID",
      extractedData: eventData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

