import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the 'Join Event' button to be visible and click it
    await page.waitForSelector('button.btn.btn-secondary.btn-large');
    await page.click('button.btn.btn-secondary.btn-large');

    // Take screenshot after clicking the button
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_join_event_clicked.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_join_event_clicked.png'));

    // Verify if the join modal is opened by checking for a specific element in the modal
    const modalVisible = await page.isVisible('selector-for-modal-element'); // Replace with actual selector
    if (!modalVisible) {
      throw new Error('Join Event modal did not open.');
    }

    return {
      success: true,
      action: "Opened the Join Event modal successfully",
      extractedData: {},
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the 'Join Event' button to be visible and click it
    await page.waitForSelector('button.btn.btn-secondary.btn-large', { timeout: 5000 });
    await page.click('button.btn.btn-secondary.btn-large');

    // Wait for the join modal to appear
    await page.waitForSelector('.modal-content', { timeout: 5000 });

    // Take screenshot of the join modal
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_join_modal.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_join_modal.png'));

    // Verify the modal has opened by checking for an input field inside the modal
    const inputVisible = await page.isVisible('#eventCode');

    return {
      success: true,
      action: "Opened the Join Event modal",
      extractedData: { modalOpened: inputVisible },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

