import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the 'Join Event' button to be visible and click it
    await page.waitForSelector('button.btn.btn-secondary.btn-large');
    await page.click('button.btn.btn-secondary.btn-large');

    // Take screenshot after clicking the button
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_join_event_clicked.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_join_event_clicked.png'));

    // Verify if the join modal is opened by checking for a specific element in the modal
    await page.waitForSelector('button.btn.btn-primary'); // Assuming this button is in the modal
    const modalVisible = await page.isVisible('button.btn.btn-primary');

    return {
      success: true,
      action: "Opened the Join Event modal",
      extractedData: { modalVisible },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

