import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the "Join Event" button to be visible and click it
    await page.waitForSelector('button.btn.btn-secondary.btn-large');
    await page.click('button.btn.btn-secondary.btn-large');

    // Take screenshot after clicking the button
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_join_event_clicked.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_join_event_clicked.png'));

    // Verify that the join modal is opened
    await page.waitForSelector('div.modal'); // Assuming the modal has a class 'modal'
    const modalVisible = await page.isVisible('div.modal');

    return {
      success: modalVisible,
      action: modalVisible ? "Join Event modal opened successfully" : "Join Event modal did not open",
      extractedData: { modalVisible },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

