import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the 'Join Event' button to be visible and click it
    await page.waitForSelector('button.btn.btn-secondary.btn-large');
    await page.click('button.btn.btn-secondary.btn-large');

    // Take a screenshot after clicking the button to open the modal
    await page.waitForSelector('.modal'); // Assuming the modal has a class 'modal'
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_modal_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_modal_opened.png'));

    // Verify that the modal is opened by checking its visibility
    const isModalVisible = await page.isVisible('.modal');

    return {
      success: true,
      action: "Opened the Join Event modal",
      extractedData: { modalOpened: isModalVisible },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

