import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the Join Event modal to be visible
    await page.waitForSelector('button.modal-close');

    // Click the close button of the modal
    await page.click('button.modal-close');

    // Wait for the modal to be closed
    await page.waitForSelector('button.modal-close', { state: 'hidden' });

    // Take a screenshot after closing the modal
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_modal_closed.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_modal_closed.png'));

    return {
      success: true,
      action: "Closed the Join Event modal",
      extractedData: {},
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

