import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the Join Event modal's close button to be visible
    await page.waitForSelector('button.modal-close', { timeout: 5000 });

    // Click the close button on the Join Event modal
    await page.click('button.modal-close');

    // Take a screenshot after closing the modal
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_modal_closed.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_modal_closed.png'));

    // Check if the modal is closed by confirming the button is no longer visible
    const isModalClosed = await page.isHidden('button.modal-close');

    return {
      success: true,
      action: "Closed the Join Event modal successfully.",
      extractedData: { modalClosed: isModalClosed },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to close the Join Event modal",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
