import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the Join Event modal to open
    await page.waitForSelector('button.btn.btn-secondary.btn-large', { timeout: 5000 });

    // Click the "Join Event" button to open the modal
    await page.click('button.btn.btn-secondary.btn-large');

    // Wait for the modal close button to be visible
    await page.waitForSelector('button.modal-close', { timeout: 5000 });

    // Take a screenshot of the modal before closing
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_modal_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_modal_open.png'));

    // Click the "X" button to close the modal
    await page.click('button.modal-close');

    // Wait for a moment to ensure the modal has closed
    await page.waitForTimeout(500);

    // Take a final screenshot after closing the modal
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_modal_closed.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_modal_closed.png'));

    return {
      success: true,
      action: "Closed the Join Event modal successfully",
      extractedData: {},
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
