import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the modal close button to be visible
    await page.waitForSelector('button.modal-close', { timeout: 5000 });

    // Click the close button to close the Join Event modal
    await page.click('button.modal-close');

    // Take a screenshot after closing the modal
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_modal_closed.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_modal_closed.png'));

    // Verify if the modal is closed by checking the absence of the modal element
    const isModalClosed = await page.evaluate(() => {
      const modal = document.querySelector('.modal');
      return modal === null;
    });

    return {
      success: true,
      action: "Closed the Join Event modal by clicking the close button",
      extractedData: { modalClosed: isModalClosed },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
