import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Verify user statistics for jun in the stat cards
    await page.waitForSelector('.quick-stats');

    // Extract the statistics
    const eventsCreated = await page.$eval('.stat-card:nth-child(1) .stat-number', el => el.textContent);
    const totalParticipants = await page.$eval('.stat-card:nth-child(2) .stat-number', el => el.textContent);
    const activeEvents = await page.$eval('.stat-card:nth-child(3) .stat-number', el => el.textContent);

    // Log the extracted data
    console.log(`Events Created: ${eventsCreated}`);
    console.log(`Total Participants: ${totalParticipants}`);
    console.log(`Active Events: ${activeEvents}`);

    // Take a screenshot of the statistics
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_statistics.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_statistics.png'));

    // Validate the statistics
    const expectedStats = {
      eventsCreated: '12',
      totalParticipants: '48',
      activeEvents: '7'
    };

    const isValid = eventsCreated === expectedStats.eventsCreated &&
                    totalParticipants === expectedStats.totalParticipants &&
                    activeEvents === expectedStats.activeEvents;

    return {
      success: isValid,
      action: "Verified user statistics on homepage",
      extractedData: {
        eventsCreated,
        totalParticipants,
        activeEvents
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

