import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Verify user statistics for jun in the stat cards
    await page.waitForSelector('.stat-number'); // Wait for stat cards to load

    // Extract the statistics
    const eventsCreated = await page.$eval('.stat-card:nth-child(1) .stat-number', el => el.textContent);
    const totalParticipants = await page.$eval('.stat-card:nth-child(2) .stat-number', el => el.textContent);
    const activeEvents = await page.$eval('.stat-card:nth-child(3) .stat-number', el => el.textContent);
    const completedEvents = await page.$eval('.stat-card:nth-child(4) .stat-number', el => el.textContent);

    // Take a screenshot after extracting data
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_statistics.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_statistics.png'));

    // Verify the extracted data against expected values
    const expectedStats = {
      eventsCreated: '12',
      totalParticipants: '48',
      activeEvents: '7',
      completedEvents: '5'
    };

    const isSuccess = eventsCreated === expectedStats.eventsCreated &&
                     totalParticipants === expectedStats.totalParticipants &&
                     activeEvents === expectedStats.activeEvents &&
                     completedEvents === expectedStats.completedEvents;

    return {
      success: isSuccess,
      action: "Verified user statistics for jun in the stat cards",
      extractedData: {
        eventsCreated,
        totalParticipants,
        activeEvents,
        completedEvents
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

