import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Verify the user statistics for jun in the stat cards
    await page.waitForSelector('.quick-stats'); // Wait for the stats section to load

    // Extract the statistics
    const eventsCreated = await page.$eval('.stat-card:nth-child(1) .stat-number', el => el.textContent);
    const totalParticipants = await page.$eval('.stat-card:nth-child(2) .stat-number', el => el.textContent);
    const activeEvents = await page.$eval('.stat-card:nth-child(3) .stat-number', el => el.textContent);
    const completedEvents = await page.$eval('.stat-card:nth-child(4) .stat-number', el => el.textContent);

    // Take a screenshot of the stats section
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_stats_section.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_stats_section.png'));

    // Verify the extracted data
    const expectedStats = {
      eventsCreated: '12',
      totalParticipants: '48',
      activeEvents: '7',
      completedEvents: '5'
    };

    const extractedData = {
      eventsCreated,
      totalParticipants,
      activeEvents,
      completedEvents
    };

    const isSuccess = 
      eventsCreated === expectedStats.eventsCreated &&
      totalParticipants === expectedStats.totalParticipants &&
      activeEvents === expectedStats.activeEvents &&
      completedEvents === expectedStats.completedEvents;

    return {
      success: isSuccess,
      action: isSuccess ? "User statistics verified successfully" : "User statistics verification failed",
      extractedData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

