import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the user statistics to be visible
    await page.waitForSelector('.quick-stats');

    // Extract user statistics
    const eventsCreated = await page.textContent('.stat-card:nth-child(1) .stat-number');
    const totalParticipants = await page.textContent('.stat-card:nth-child(2) .stat-number');
    const activeEvents = await page.textContent('.stat-card:nth-child(3) .stat-number');

    // Take a screenshot of the statistics section
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_stats.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_stats.png'));

    // Verify that the statistics are as expected
    const expectedStats = {
      eventsCreated: '12',
      totalParticipants: '48',
      activeEvents: '7'
    };

    const extractedData = {
      eventsCreated,
      totalParticipants,
      activeEvents
    };

    const isSuccess = eventsCreated === expectedStats.eventsCreated &&
                     totalParticipants === expectedStats.totalParticipants &&
                     activeEvents === expectedStats.activeEvents;

    return {
      success: isSuccess,
      action: isSuccess ? "User statistics verified successfully" : "User statistics verification failed",
      extractedData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
