import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the user statistics to load
    await page.waitForSelector('.quick-stats');

    // Extract user statistics
    const eventsCreated = await page.locator('.stat-number').nth(0).innerText();
    const totalParticipants = await page.locator('.stat-number').nth(1).innerText();
    const activeEvents = await page.locator('.stat-number').nth(2).innerText();

    // Take screenshot of the statistics section
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_stats.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_stats.png'));

    // Return the extracted data
    return {
      success: true,
      action: "User statistics displayed successfully",
      extractedData: {
        eventsCreated: parseInt(eventsCreated),
        totalParticipants: parseInt(totalParticipants),
        activeEvents: parseInt(activeEvents)
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
