import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click on the "Join Event" button to open the join modal
    await page.waitForSelector('button.btn.btn-secondary.btn-large');
    await page.click('button.btn.btn-secondary.btn-large');

    // Take a screenshot of the modal
    await page.waitForSelector('#eventCode');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_join_modal.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_join_modal.png'));

    // Fill in the event code
    await page.fill('#eventCode', 'TEAM2024');

    // Fill in the participant name
    await page.fill('input[name="undefined"]', 'jun');

    // Click the "Join Event" button in the modal
    await page.waitForSelector('button.btn.btn-primary');
    await page.click('button.btn.btn-primary');

    // Wait for some confirmation or indication that the event was joined
    await page.waitForLoadState('networkidle');

    // Take a final screenshot to confirm the action
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_event_joined.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_event_joined.png'));

    return {
      success: true,
      action: "Joined the event with code TEAM2024",
      extractedData: {},
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

