    await page.waitForSelector('#eventCode'); // Wait for the modal to open

    // Input the event code into the input field
    await page.fill('#eventCode', 'TEAM2024');

    // Input participant name
    await page.fill('[name="undefined"][placeholder="undefined"]', 'jun');

    // Click on the "Join Event" button in the modal
    await page.waitForSelector('button.btn.btn-primary');
    await page.click('button.btn.btn-primary');

    // Take a screenshot after attempting to join the event
    await page.waitForTimeout(2000); // Wait for the action to complete
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_join_event.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_join_event.png'));

    // Verify that the event was joined successfully (this can depend on how successful joins are indicated in the UI)
    const successMessage = await page.waitForSelector('text=You have successfully joined the event', { timeout: 5000 });
    const extractedData = {
      message: await successMessage.textContent()
    };

    return {
      success: true,
      action: "Successfully joined the event with code TEAM2024",
      extractedData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
