import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the Recent Events section to load
    await page.waitForSelector('#recentEventsGrid');

    // Extract recent events data
    const events = await page.$$eval('#recentEventsGrid .event-card', cards => {
      return cards.map(card => ({
        title: card.querySelector('.event-title') ? card.querySelector('.event-title').innerText : null,
        date: card.querySelector('.event-date') ? card.querySelector('.event-date').innerText : null,
        creator: card.querySelector('.event-creator') ? card.querySelector('.event-creator').innerText : null,
      }));
    });

    // Take a screenshot of the Recent Events section
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_recent_events.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_recent_events.png'));

    // Check if events are displayed
    const success = events.length > 0;

    return {
      success,
      action: success ? "Recent events are displayed successfully" : "No recent events found",
      extractedData: { events },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

