import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the Recent Events section to load
    await page.waitForSelector('#recentEventsGrid');

    // Extract recent events data
    const events = await page.$$eval('#recentEventsGrid .event-card', cards => {
      return cards.map(card => ({
        title: card.querySelector('.event-title')?.innerText || '',
        date: card.querySelector('.event-date')?.innerText || '',
        creator: card.querySelector('.event-creator')?.innerText || ''
      }));
    });

    // Take a screenshot of the Recent Events section
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_recent_events.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_recent_events.png'));

    // Verify that events are displayed
    const success = events.length > 0;

    return {
      success,
      action: success ? "Recent events are displayed successfully" : "No recent events found",
      extractedData: { events },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the Recent Events section to be visible
    await page.waitForSelector('#recentEventsGrid', { timeout: 5000 });

    // Verify if event cards are present in the Recent Events section
    const eventCards = await page.$$('.event-card');
    if (eventCards.length > 0) {
      // Take a screenshot of the Recent Events section
      await page.screenshot({ path: path.join(screenshotsDir, 'step_2_recent_events.png') });
      screenshots.push(path.join(screenshotsDir, 'step_2_recent_events.png'));

      // Extracting data about the events
      const extractedData = [];
      for (const card of eventCards) {
        const title = await card.$eval('.event-title', el => el.textContent.trim());
        const date = await card.$eval('.event-date', el => el.textContent.trim());
        extractedData.push({ title, date });
      }

      return {
        success: true,
        action: "Verified that recent events are displayed in the Recent Events section",
        extractedData,
        screenshots,
        error: null
      };
    } else {
      return {
        success: false,
        action: "No recent events found in the Recent Events section",
        extractedData: {},
        screenshots,
        error: "No event cards found"
      };
    }
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

