import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the Recent Events section to load
    await page.waitForSelector('#recentEventsGrid');

    // Verify that recent events are displayed
    const eventCards = await page.$$('.event-card');
    const eventCount = eventCards.length;

    // Take a screenshot of the Recent Events section
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_recent_events.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_recent_events.png'));

    // Extract meaningful data to verify task completion
    const extractedData = {
      eventCount,
      eventsDisplayed: eventCount > 0
    };

    return {
      success: true,
      action: "Verified that recent events are displayed in the Recent Events section",
      extractedData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the Recent Events section to be visible
    await page.waitForSelector('#recentEventsGrid', { state: 'visible' });

    // Check if event cards are present
    const eventCards = await page.$$('.event-card');
    const eventCount = eventCards.length;

    // Take a screenshot of the Recent Events section
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_recent_events.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_recent_events.png'));

    // Extract meaningful data to verify task completion
    const eventTitles = await Promise.all(eventCards.map(async (card) => {
      const titleElement = await card.$('h3.event-title'); // Assumed title selector
      return titleElement ? page.evaluate(el => el.innerText, titleElement) : null;
    }));

    return {
      success: true,
      action: "Verified recent events are displayed in the Recent Events section",
      extractedData: {
        eventCount,
        eventTitles: eventTitles.filter(Boolean) // Filter out nulls
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the Recent Events grid to be visible
    await page.waitForSelector('#recentEventsGrid .event-card', { timeout: 5000 });

    // Extract the event cards
    const eventCards = await page.$$eval('#recentEventsGrid .event-card', cards => {
      return cards.map(card => {
        const title = card.querySelector('.event-title') ? card.querySelector('.event-title').innerText : '';
        const date = card.querySelector('.event-date') ? card.querySelector('.event-date').innerText : '';
        return { title, date };
      });
    });

    // Verify if recent events are displayed
    if (eventCards.length > 0) {
      // Take a screenshot of the Recent Events section
      await page.screenshot({ path: path.join(screenshotsDir, 'step_2_events_displayed.png') });
      screenshots.push(path.join(screenshotsDir, 'step_2_events_displayed.png'));

      return {
        success: true,
        action: "Recent events successfully displayed",
        extractedData: { eventCards },
        screenshots,
        error: null
      };
    } else {
      return {
        success: false,
        action: "No recent events found",
        extractedData: {},
        screenshots,
        error: "No recent events are currently displayed in the grid."
      };
    }
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
