import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the Recent Events section to load
    await page.waitForSelector('#recentEventsGrid');

    // Extract recent events data
    const events = await page.$$eval('.event-card', cards => {
      return cards.map(card => {
        const title = card.querySelector('.event-title')?.innerText || '';
        const date = card.querySelector('.event-date')?.innerText || '';
        return { title, date };
      });
    });

    // Log the extracted events for verification
    console.log('Recent Events:', events);

    // Take a screenshot of the Recent Events
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_recent_events.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_recent_events.png'));

    return {
      success: true,
      action: "Viewed recent events and extracted data.",
      extractedData: { events },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
