import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the Recent Events section to load
    await page.waitForSelector('#recentEventsGrid');

    // Extract recent events data
    const events = await page.$$eval('#recentEventsGrid .event-card', cards => {
      return cards.map(card => {
        const title = card.querySelector('.event-title')?.innerText || '';
        const date = card.querySelector('.event-date')?.innerText || '';
        return { title, date };
      });
    });

    // Take a screenshot of the Recent Events section
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_recent_events.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_recent_events.png'));

    // Check if events are displayed
    const success = events.length > 0;

    return {
      success,
      action: success ? "Recent events displayed successfully" : "No recent events found",
      extractedData: { events },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the Recent Events section to load
    await page.waitForSelector('#recentEventsGrid');

    // Check if event cards are present
    const eventCards = await page.$$('.event-card');
    if (eventCards.length === 0) {
      throw new Error('No recent events found in the Recent Events section.');
    }

    // Extract data from the event cards
    const extractedData = [];
    for (const card of eventCards) {
      const titleElement = await card.$('.event-title');
      const dateElement = await card.$('.event-date');
      const title = await page.evaluate(el => el.textContent, titleElement);
      const date = await page.evaluate(el => el.textContent, dateElement);
      extractedData.push({ title, date });
    }

    // Take a screenshot of the Recent Events section
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_recent_events.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_recent_events.png'));

    return {
      success: true,
      action: "Verified that recent events are displayed in the Recent Events section",
      extractedData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

