import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the Public Events section to load
    await page.waitForSelector('#publicEventsGrid');

    // Extract event cards
    const events = await page.$$eval('#publicEventsGrid .event-card', cards => {
      return cards.map(card => {
        const title = card.querySelector('.event-title')?.innerText || '';
        const date = card.querySelector('.event-date')?.innerText || '';
        return { title, date };
      });
    });

    // Take screenshot of the events displayed
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_events_displayed.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_events_displayed.png'));

    // Verify that events are displayed
    const success = events.length > 0;

    return {
      success,
      action: success ? "Successfully verified public events are displayed." : "No public events found.",
      extractedData: events,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

