import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the public events grid to be visible
    await page.waitForSelector('#publicEventsGrid .event-card');

    // Extract data from the public events section
    const events = await page.$$eval('#publicEventsGrid .event-card', cards => {
      return cards.map(card => {
        const title = card.querySelector('.event-title')?.innerText || '';
        const date = card.querySelector('.event-date')?.innerText || '';
        return { title, date };
      });
    });

    // Take a screenshot of the events grid
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_events_grid.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_events_grid.png'));

    // Verify that events are displayed
    const success = events.length > 0;

    return {
      success,
      action: success ? "Public events are displayed successfully" : "No public events found",
      extractedData: { events },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

