import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the public events section to load
    await page.waitForSelector('#publicEventsGrid');

    // Extract the event cards
    const events = await page.$$eval('.event-card', cards => {
      return cards.map(card => ({
        title: card.querySelector('.event-title') ? card.querySelector('.event-title').innerText : null,
        date: card.querySelector('.event-date') ? card.querySelector('.event-date').innerText : null,
        time: card.querySelector('.event-time') ? card.querySelector('.event-time').innerText : null,
      }));
    });

    // Take a screenshot of the events
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_events.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_events.png'));

    // Verify the events are displayed
    if (events.length > 0) {
      return {
        success: true,
        action: "Successfully retrieved public events from the grid",
        extractedData: { events },
        screenshots,
        error: null
      };
    } else {
      return {
        success: false,
        action: "No events found in the public events grid",
        extractedData: {},
        screenshots,
        error: "No events displayed"
      };
    }
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
