import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the public events grid to load
    await page.waitForSelector('#publicEventsGrid');

    // Take a screenshot of the public events section
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_events_grid.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_events_grid.png'));

    // Extract event cards data
    const events = await page.$$eval('#publicEventsGrid .event-card', cards => {
      return cards.map(card => {
        const title = card.querySelector('.event-title')?.innerText || '';
        const date = card.querySelector('.event-date')?.innerText || '';
        return { title, date };
      });
    });

    // Verify that events are displayed
    const success = events.length > 0;

    return {
      success,
      action: success ? "Public events displayed successfully" : "No public events found",
      extractedData: { events },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the public events grid to load
    await page.waitForSelector('#eventSearchInput'); // Wait for the search input to be visible

    // Check if public events are displayed
    const events = await page.$$('.event-card'); // Using class selector to find event cards
    const eventCount = events.length;

    // Take a screenshot of the events displayed
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_events_displayed.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_events_displayed.png'));

    // Extracting titles of events for verification
    const eventTitles = await Promise.all(events.map(async (event) => {
      const titleElement = await event.$('h3'); // Assuming event title is in an h3 tag
      return titleElement ? await titleElement.innerText() : null;
    }));

    return {
      success: true,
      action: `Displayed ${eventCount} public events`,
      extractedData: { eventCount, eventTitles },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

