import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the public events grid to be visible
    await page.waitForSelector('#publicEventsGrid');

    // Extract event cards
    const eventCards = await page.$$eval('#publicEventsGrid .event-card', cards => {
      return cards.map(card => {
        const title = card.querySelector('.event-title')?.textContent || '';
        const date = card.querySelector('.event-date')?.textContent || '';
        return { title, date };
      });
    });

    // Log the extracted data for verification
    console.log('Extracted Event Cards:', eventCards);

    // Take a screenshot of the public events grid
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_events_grid.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_events_grid.png'));

    return {
      success: true,
      action: "Verified public events are displayed in the Public Events section",
      extractedData: { eventCards },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

