import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the public events grid section to load
    await page.waitForSelector('#publicEventsGrid');

    // Check if event cards are present
    const eventCards = await page.$$('.event-card');

    // Take a screenshot of the events grid
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_events_grid.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_events_grid.png'));

    // Extract data from event cards
    const extractedData = [];
    for (const card of eventCards) {
      const title = await card.$eval('.event-title', el => el.textContent.trim());
      const date = await card.$eval('.event-date', el => el.textContent.trim());
      extractedData.push({ title, date });
    }

    return {
      success: true,
      action: "Verified that public events are displayed in the Public Events section",
      extractedData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
