import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the public events grid to be visible
    await page.waitForSelector('#publicEventsGrid');

    // Extract event cards
    const eventCards = await page.$$eval('.event-card', cards => {
      return cards.map(card => {
        const title = card.querySelector('.event-title') ? card.querySelector('.event-title').innerText : null;
        const date = card.querySelector('.event-date') ? card.querySelector('.event-date').innerText : null;
        return { title, date };
      });
    });

    // Take screenshot of the events grid
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_events_grid.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_events_grid.png'));

    // Verify that public events are displayed
    const success = eventCards.length > 0;

    return {
      success,
      action: success ? "Public events displayed successfully" : "No public events found",
      extractedData: { events: eventCards },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

