import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Verify the username is displayed correctly
    await page.waitForSelector('.username');
    const username = await page.$eval('.username', el => el.textContent.trim());

    // Verify the user avatar is displayed correctly
    await page.waitForSelector('.user-avatar img');
    const avatarVisible = await page.$eval('.user-avatar img', el => el.complete && el.naturalHeight !== 0);

    // Take screenshot after verifying elements
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_verification.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_verification.png'));

    // Check if the expected username and avatar visibility are correct
    const success = username === 'jun' && avatarVisible;

    return {
      success,
      action: "Verified user profile display with username 'jun' and avatar visibility",
      extractedData: {
        username,
        avatarVisible
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

