import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Verify username
    await page.waitForSelector('.username');
    const username = await page.$eval('.username', el => el.textContent.trim());
    if (username !== 'jun') {
      throw new Error(`Expected username 'jun', but found '${username}'`);
    }

    // Take screenshot of username
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_username_verified.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_username_verified.png'));

    // Verify user avatar
    await page.waitForSelector('.user-avatar img');
    const avatarSrc = await page.$eval('.user-avatar img', img => img.src);
    if (!avatarSrc.includes('data:image/svg+xml;base64')) {
      throw new Error('User avatar not found or is not a valid base64 image');
    }

    // Take screenshot of user avatar
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_avatar_verified.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_avatar_verified.png'));

    return {
      success: true,
      action: "Verified user profile displays correct username and avatar",
      extractedData: { username, avatarSrc },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the user avatar and username to be visible
    await page.waitForSelector('.user-avatar img');
    await page.waitForSelector('.username');

    // Extract username and avatar source
    const username = await page.innerText('.username');
    const avatarSrc = await page.getAttribute('.user-avatar img', 'src');

    // Verify the username and avatar
    const isUsernameCorrect = username === 'jun';
    const isAvatarVisible = avatarSrc.includes('data:image/svg+xml;base64');

    // Take screenshot after extracting data
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_user_profile.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_user_profile.png'));

    return {
      success: isUsernameCorrect && isAvatarVisible,
      action: "Verified user profile display",
      extractedData: {
        username,
        avatarSrc
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

