import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Verify the username is displayed correctly
    await page.waitForSelector('.username');
    const username = await page.$eval('.username', el => el.textContent);
    const isUsernameCorrect = username === 'jun';

    // Verify the avatar is displayed
    await page.waitForSelector('.user-avatar img');
    const avatarVisible = await page.isVisible('.user-avatar img');

    // Take a screenshot after verification
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_verification.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_verification.png'));

    return {
      success: isUsernameCorrect && avatarVisible,
      action: "Verified user profile displays correct username and avatar",
      extractedData: {
        username,
        avatarVisible
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the username element to be visible
    await page.waitForSelector('.username');
    // Extract the username
    const username = await page.$eval('.username', el => el.textContent.trim());

    // Wait for the avatar image to be visible
    await page.waitForSelector('.user-avatar img');
    // Extract the avatar source
    const avatarSrc = await page.$eval('.user-avatar img', img => img.src);

    // Verify if the extracted username and avatar are correct
    const isUsernameCorrect = username === 'jun';
    const isAvatarPresent = avatarSrc.includes('data:image/svg+xml;base64');

    // Take a screenshot after verification
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_verification.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_verification.png'));

    return {
      success: isUsernameCorrect && isAvatarPresent,
      action: "Verified the user profile display with username and avatar",
      extractedData: {
        username,
        avatarSrc,
        isUsernameCorrect,
        isAvatarPresent
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the username element to be visible
    await page.waitForSelector('.username');

    // Verify the username
    const username = await page.textContent('.username');
    const avatarVisible = await page.isVisible('.user-avatar img');

    // Take screenshot after verifying elements
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_username_verification.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_username_verification.png'));

    // Validate the username and avatar
    if (username.trim() === 'jun' && avatarVisible) {
      return {
        success: true,
        action: "Verified that the user profile displays 'jun' username and user avatar correctly.",
        extractedData: { username, avatarVisible },
        screenshots,
        error: null
      };
    } else {
      throw new Error("Username or avatar verification failed.");
    }
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
