    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_user_profile.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_user_profile.png'));

    return {
      success: isUsernameCorrect && isAvatarCorrect,
      action: "Verified user profile display",
      extractedData: { username, avatarSrc },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

