import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the username element to be visible
    await page.waitForSelector('.username');
    const username = await page.$eval('.username', el => el.textContent);

    // Wait for the user avatar to be visible
    await page.waitForSelector('.user-avatar img');
    const avatarSrc = await page.$eval('.user-avatar img', el => el.src);

    // Verify the username and avatar
    const isUsernameCorrect = username === 'jun';
    const isAvatarPresent = avatarSrc.includes('data:image/svg+xml;base64');

    // Take a screenshot after verification
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_verification.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_verification.png'));

    return {
      success: isUsernameCorrect && isAvatarPresent,
      action: "Verified user profile display with username and avatar",
      extractedData: {
        username,
        avatarSrc
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

