import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the username element to be visible
    await page.waitForSelector('.username');
    const username = await page.$eval('.username', el => el.textContent);

    // Wait for the user avatar element to be visible
    await page.waitForSelector('.user-avatar img');
    const avatarSrc = await page.$eval('.user-avatar img', el => el.src);

    // Take a screenshot after verifying elements
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_verified.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_verified.png'));

    // Verify the expected results
    const expectedUsername = 'jun';
    const isUsernameCorrect = username === expectedUsername;
    const isAvatarVisible = avatarSrc.includes('data:image/svg+xml;base64');

    return {
      success: isUsernameCorrect && isAvatarVisible,
      action: "Verified user profile display with username and avatar",
      extractedData: {
        username,
        avatarSrc,
        isUsernameCorrect,
        isAvatarVisible
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
