    return {
      success: true,
      action: "Verified user profile displays the correct username and avatar",
      extractedData: {
        username,
        avatarSrc
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
