import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the user avatar to be visible
    await page.waitForSelector('.user-avatar img');

    // Verify the username is displayed correctly
    const usernameVisible = await page.isVisible('.username');
    const usernameText = await page.textContent('.username');
    const avatarVisible = await page.isVisible('.user-avatar img');

    // Take a screenshot of the user profile display
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_user_profile.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_user_profile.png'));

    // Check if the username is 'jun' and the avatar is displayed
    const isUsernameCorrect = usernameText.trim() === 'jun';

    return {
      success: usernameVisible && avatarVisible && isUsernameCorrect,
      action: "Verified user profile display with username 'jun' and avatar",
      extractedData: {
        username: usernameText.trim(),
        avatarVisible: avatarVisible
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
