import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the user avatar and username to be visible
    await page.waitForSelector('.user-avatar img');
    await page.waitForSelector('.username');

    // Extract the username and avatar source
    const username = await page.textContent('.username');
    const avatarSrc = await page.getAttribute('.user-avatar img', 'src');

    // Take screenshot of the user profile display
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_user_profile.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_user_profile.png'));

    // Verify the username and avatar
    const isUsernameCorrect = username === 'jun';
    const isAvatarPresent = avatarSrc.includes('data:image/svg+xml;base64');

    return {
      success: isUsernameCorrect && isAvatarPresent,
      action: "Verified user profile display with username and avatar",
      extractedData: {
        username,
        avatarSrc
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

