import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the username to be visible and check its text content
    await page.waitForSelector('.username');
    const username = await page.$eval('.username', el => el.textContent.trim());

    // Wait for the user avatar to be visible
    await page.waitForSelector('.user-avatar img');
    const avatarVisible = await page.$eval('.user-avatar img', img => img.complete && img.naturalHeight !== 0);

    // Take screenshot after verification
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_profile_verification.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_profile_verification.png'));

    // Verify expected values
    const isUsernameCorrect = username === 'jun';
    const isAvatarVisible = avatarVisible;

    return {
      success: isUsernameCorrect && isAvatarVisible,
      action: "Verified the user profile display with username 'jun' and avatar visible",
      extractedData: {
        username,
        avatarVisible
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
