import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Verify the username is displayed correctly
    await page.waitForSelector('.username');
    const username = await page.$eval('.username', el => el.textContent);

    // Verify the user avatar is displayed
    await page.waitForSelector('.user-avatar img');
    const avatarVisible = await page.isVisible('.user-avatar img');

    // Take screenshot after verification
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_verification.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_verification.png'));

    // Check if the username and avatar are correct
    const isUsernameCorrect = username === 'jun';
    const isAvatarVisible = avatarVisible;

    return {
      success: isUsernameCorrect && isAvatarVisible,
      action: "Verified user profile display with username 'jun' and avatar.",
      extractedData: {
        username,
        avatarVisible: isAvatarVisible
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

