import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the user profile section in the header
    await page.waitForSelector('.header .user-menu', { timeout: 5000 });

    // Wait for the username span
    const usernameSelector = '.header .user-menu .username';
    await page.waitForSelector(usernameSelector, { timeout: 3000 });

    // Extract username text
    const username = await page.$eval(usernameSelector, el => el.textContent.trim());

    // Wait for the avatar image
    const avatarSelector = '.header .user-menu .user-avatar img';
    await page.waitForSelector(avatarSelector, { timeout: 3000 });

    // Extract avatar src attribute
    const avatarSrc = await page.$eval(avatarSelector, el => el.getAttribute('src'));

    // Take screenshot of the user menu area
    const userMenuElement = await page.$('.header .user-menu');
    if (userMenuElement) {
      await userMenuElement.screenshot({ path: path.join(screenshotsDir, 'step_2_user_profile.png') });
      screenshots.push(path.join(screenshotsDir, 'step_2_user_profile.png'));
    }

    // Verify username and avatar
    const usernameIsJun = username === 'jun';
    const avatarIsPresent = typeof avatarSrc === 'string' && avatarSrc.startsWith('data:image/svg+xml;base64,');

    // Compose extracted data
    const extractedData = {
      username,
      avatarSrc,
      usernameIsJun,
      avatarIsPresent
    };

    // Final result
    return {
      success: usernameIsJun && avatarIsPresent,
      action: "Verified that the user profile in the header displays the username 'jun' and a user avatar image.",
      extractedData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

